import NoteContext from "./NoteContext";
import React, { useState } from 'react';

const NoteState = (props) => {
  const host = "http://localhost:5000"
  const noteInitial = [{
    "_id": "9967288a15e51f45ed2ed26a27",
    "user": "6721b51769ef9c9ff1ec1f29",
    "title": "React",
    "description": "React is a popular JavaScript library developed by Facebook for building user interfaces, especially single-page applications where fast, dynamic updates are crucial. It uses a component-based architecture, allowing developers to create reusable UI elements that manage their own state. This modularity makes complex applications easier to manage, test, and maintain. React’s virtual DOM ensures high performance by efficiently updating only the parts of the page that change, rather than reloading the entire page. Paired with tools like Redux for state management and Next.js for server-side rendering, React is highly adaptable and widely used in modern web development.",
    "tag": "personal",
    "date": "2024-11-04T08:47:17.359Z",
    "__v": 0
  }]

  const [notes,setNotes] = useState(noteInitial);

  //Add Note
  const addNote = (title,description,tag) =>
  {
    //Left: fetch APIs.
    const note = {"_id": "0067288a15e51f45ed2ed26a27",
    "user": "6721b51769ef9c9ff1ec1f29",
    "title": title,
    "description": description,
    "tag": tag,
    "date": "2024-11-04T08:47:17.359Z",
    "__v": 0}
    setNotes(notes.concat(note))
  }

  //Update Note
  const updateNote = async (title,description,tag,id) =>
    {
      //API Call
      const response = await fetch(`${host}/note/updatenote/${id}`, {
        method: "POST",
        body: JSON.stringify({ username: "example" }),
        headers: {
          "Content-Type": "application/json",
          "auth-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjcyMWI1MTc2OWVmOWM5ZmYxZWMxZjI5In0sImlhdCI6MTczMDI2MjU1M30.YpiTSZIJ9xjiajL-tS8o8bLShH9r2TthJsihPefzVlU"
        },
      });

      //Logic to edit the note
      console.log(response.status);
      for (let index = 0; index < notes.length; index++) {
        const element = notes[index];
        if(element._id===id)
        {
          element.title=title;
          element.description=description;
          element.tag=tag;
        }
      }
    }

  //Delete Note
const deleteNote = (id) =>
  {
    const newNotes = notes.filter((note)=>{return note._id !== id})
    setNotes(newNotes);
  }

  return (
    <>
    <NoteContext.Provider value = {{notes,addNote,updateNote,deleteNote}}>
    {props.children}
    </NoteContext.Provider>
    </>
  )
}

export default NoteState
