import React, { useContext } from "react";
import { AlertContext } from "../context/alerts/AlertContext";

const Alert = () => {
  const { alert } = useContext(AlertContext);

  return (
    alert.message && (
      <div
        className={`alert alert-${alert.type} d-flex align-items-center`}
        role="alert"
      >
        <span className="me-2" aria-hidden="true">
          {alert.icon === "check" ? (
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="16"
              height="16"
              fill="currentColor"
              className="bi bi-check-circle-fill"
              viewBox="0 0 16 16"
            >
              <path d="M16 8A8 8 0 1 1 0 8a8 8 0 0 1 16 0zm-3.97-3.03a.75.75 0 0 0-1.08.022L7.477 9.417 5.384 7.323a.75.75 0 0 0-1.06 1.06L6.97 11.03a.75.75 0 0 0 1.079-.02l3.992-4.99a.75.75 0 0 0-.01-1.05z" />
            </svg>
          ) : (
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="16"
              height="16"
              fill="currentColor"
              className="bi bi-info-fill"
              viewBox="0 0 16 16"
            >
              <path d="M8 16A8 8 0 1 0 8 0a8 8 0 0 0 0 16zM8.93 6.588l-2.29-.287-.082-.38a.965.965 0 0 1 .287-.804c.223-.222.536-.33.803-.288l.808.162c.42.084.753.42.815.84l.162.81a.965.965 0 0 1-.287.804c-.223.222-.536.33-.803.288zM6.293 8.293a1 1 0 0 1 1.414 1.414l-1 1A1 1 0 0 1 6.293 9.707l1-1z" />
            </svg>
          )}
        </span>
        <div>{alert.message}</div>
      </div>
    )
  );
};

export default Alert;
