import NoteContext from "./NoteContext";
import React, { useState } from "react";

const NoteState = (props) => {
  const host = "http://localhost:5000";
  const noteInitial = [];
  const [notes, setNotes] = useState(noteInitial);

  const getNote = async (title, description, tag, id) => {
    //API Call
    const response = await fetch(`${host}/note/updatenote/${id}`, {
      method: "PUT",
      body: JSON.stringify({ title, description, tag }),
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjcyMWI1MTc2OWVmOWM5ZmYxZWMxZjI5In0sImlhdCI6MTczMDI2MjU1M30.YpiTSZIJ9xjiajL-tS8o8bLShH9r2TthJsihPefzVlU",
      },
    });
  };

  //Add Note
  const addNote = async (title, description, tag) => {
    //API Call
    const response = await fetch(`${host}/note/createnote`, {
      method: "POST",
      body: JSON.stringify({ title, description, tag }),
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjcyMWI1MTc2OWVmOWM5ZmYxZWMxZjI5In0sImlhdCI6MTczMDI2MjU1M30.YpiTSZIJ9xjiajL-tS8o8bLShH9r2TthJsihPefzVlU",
      },
    });
    const note = {
      _id: "0067288a15e51f45ed2ed26a27",
      user: "6721b51769ef9c9ff1ec1f29",
      title: title,
      description: description,
      tag: tag,
      date: "2024-11-04T08:47:17.359Z",
      __v: 0,
    };
    setNotes(notes.concat(note));
  };

  //Update Note
  const updateNote = async (title, description, tag, id) => {
    //API Call
    const response = await fetch(`${host}/note/updatenote/${id}`, {
      method: "PUT",
      body: JSON.stringify({ title, description, tag }),
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjcyMWI1MTc2OWVmOWM5ZmYxZWMxZjI5In0sImlhdCI6MTczMDI2MjU1M30.YpiTSZIJ9xjiajL-tS8o8bLShH9r2TthJsihPefzVlU",
      },
    });
    console.log(response.status);

    //Logic to edit the note
    for (let index = 0; index < notes.length; index++) {
      const element = notes[index];
      if (element._id === id) {
        element.title = title;
        element.description = description;
        element.tag = tag;
      }
    }
  };

  //Delete Note
  const deleteNote = (id) => {
    const newNotes = notes.filter((note) => {
      return note._id !== id;
    });
    setNotes(newNotes);
  };

  return (
    <>
      <NoteContext.Provider value={{ notes, addNote, updateNote, deleteNote }}>
        {props.children}
      </NoteContext.Provider>
    </>
  );
};

export default NoteState;
